import React, { useEffect } from 'react'

// v9 compat packages are API compatible with v8 code

import firebase from 'firebase/compat/app';

import 'firebase/compat/auth';

import 'firebase/compat/firestore';

firebase.initializeApp(
  { 
    apiKey: "AIzaSyCpC_0_uloBHLVEUSDSrG4aLdca7Fn9tfw",
    authDomain: "chat-app-a42c5.firebaseapp.com",
    projectId: "chat-app-a42c5",
    storageBucket: "chat-app-a42c5.appspot.com",
    messagingSenderId: "780964223577",
    appId: "1:780964223577:web:b81f1d745ceb2df520ef48",
    measurementId: "G-W8YKEBDP26"
  }
)
const firestore = firebase.firestore();

function PersonalMessages(props) {

  const [messages , setMessages] = React.useState([])
  
  useEffect( () =>{
    
    setMessages( 
      fetchDatas()
    );
    
   
  } , [])

  async function fetchDatas() {
    const response = firestore.collection('Messages');
    const datas = await response.get();
    console.log(datas.docs[0].data());
    console.log(datas.docs[0].id);
    return [datas.docs[0].data()];
  }
  console.log(fetchDatas());

  const uid  = firebase.auth().currentUser.uid
  const photoURL = firebase.auth().currentUser.photoURL

  console.log(firestore.collection(`Messages`));
  return (
    <div className='personal-message'>
         
            {
                props.value.map( (oneMessage,id) => {
                  
                 return (
                 <div  className='oneMessage  text-light d-flex gap-2 m-2 justify-content-end'>
                        <p  key={id} className='bg-primary d-inline p-2 px-4 rounded-pill '>{oneMessage}</p>
                        <div  className='avatar-div'>
                           <img src={photoURL} className="img-fluid rounded-pill"/> 
                           </div>
                    </div> )
                })
            }
            
    </div>
  )
}

export default PersonalMessages
